class Meta {
    obj = null;
    test = () => {
        return fetch('http://10.0.0.12/api/usuario/consulta.php')
            .then(response => response.json())  // <- ejecuto la función que devuelve una Promesa
            .then(data => {
                let datos = data.datos
                let js = JSON.stringify(datos)
                this.obj = js;  // asigno el objeto a la propiedad obj de la Clase
            })
            .catch(console.log)  // un poquito de programación funcional :)            
    }
}

let mt = new Meta();

let User = mt.test().then(() => {
    let Users = mt.obj;
    return Users;
    //console.log(Users); // <- ahora si obj contiene lo que me interesa
});
