import React from "react";
import { Dashboard, DetectionScreen, ProfileScreen, ResultScreen, WearableScreen, SuggestionsScreen , EditProfileScreen, DetectionScreen2} from "./src/screens";
import Router from './src/routes';
import {View, ActivityIndicator} from 'react-native';
import { AuthContext } from "./src/helpers/context";
import { NavigationContainer } from "@react-navigation/native";
import {DrawerContent} from './src/components/menu';
import { createDrawerNavigator } from "@react-navigation/drawer";
import AsyncStorage from 'react-native';


const Drawer = createDrawerNavigator();

export default function App(){
  const initialLoginState = {
    isLoading: true,
    userName: null,
  };
  const loginReducer = (prevState, action) => {
    switch( action.type ) {
      case 'RETRIEVE_TOKEN': 
        return {
          ...prevState,
          isLoading: false,
        };
      case 'LOGIN': 
        return {
          ...prevState,
          userName: action.iduser,
          isLoading: false,
        };
      case 'LOGOUT': 
        return {
          ...prevState,
          userName: null,
          isLoading: false,
        };
      case 'REGISTER': 
        return {
          ...prevState,
          userName: action.iduser,
          isLoading: false,
        };
    }
  };
  const [loginState, dispatch] = React.useReducer(loginReducer, initialLoginState);
  const authContext = React.useMemo(() => ({
    signIn: async(foundUser) => {
      // setUserToken('fgkj');
      // setIsLoading(false);
      const userName = foundUser[0].username;
      const iduser = foundUser[0].iduser;
      console.log(userName)
      console.log(iduser)

      // console.log('user token: ', userToken);
      dispatch({ type: 'LOGIN', user: userName });
    },
    signOut: async() => {
      // setUserToken(null);
      // setIsLoading(false);

      dispatch({ type: 'LOGOUT' });
    },
    signUp: () => {
      // setUserToken('fgkj');
      // setIsLoading(false);
    },
    toggleTheme: () => {
      setIsDarkTheme( isDarkTheme => !isDarkTheme );
    }
  }), []);

  React.useEffect(() => {
    setTimeout(async() => {
      // setIsLoading(false);
      // console.log('user token: ', userToken);
      dispatch({ type: 'RETRIEVE_TOKEN'});
    }, 1000);
  }, []);

  if( loginState.isLoading ) {
    return(
      <View style={{flex:1,justifyContent:'center',alignItems:'center'}}>
        <ActivityIndicator size="large"/>
      </View>
    );
  }
  return(
    <AuthContext.Provider value={authContext}>
    <NavigationContainer>
      { loginState.status !== null ? (
        <Drawer.Navigator drawerContent={props => <DrawerContent {...props} />}>
          <Drawer.Screen name="Inicio" component={Dashboard} />
          <Drawer.Screen name="Detección de ECV" component={DetectionScreen} />
          <Drawer.Screen name="Detección ECV" component={DetectionScreen2} />
          <Drawer.Screen name="Perfil" component={ProfileScreen} />
          <Drawer.Screen name="Resultados" component={ResultScreen} />
          <Drawer.Screen name="Dispositivos" component={WearableScreen} />
          <Drawer.Screen name="Sugerencias" component={SuggestionsScreen} />
          <Drawer.Screen name="Editar perfil" component={EditProfileScreen} />
        </Drawer.Navigator>
      )
    :
      <Router></Router>
    }
    </NavigationContainer>
    </AuthContext.Provider>
  );
}
