import React from "react";
import {View, Text, TextInput, TouchableOpacity, Button, ScrollView,Alert} from 'react-native';
import form from '../../../styles/form';
import * as Animatable from 'react-native-animatable';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';

export function FormS() {
    
    const [data, setData] = React.useState({
        edad: 0,
        creatinine: 0,
        old_peak: 0,
        rest_ecf: 0,
        check_textInputChange: false,
    });

    const textInputChange1 = (edad) =>{
        if(edad != 0){
            setData({
                ...data,
                edad: edad,
                check_textInputChange: true
            });
        }else{
            setData({
                ...data,
                edad: edad,
                check_textInputChange: false
            });
        }
    }
    const textInputChange2 = (cre) =>{
        if(cre != 0){
            setData({
                ...data,
                creatinine: cre,
                check_textInputChange: true
            });
        }else{
            setData({
                ...data,
                creatinine: cre,
                check_textInputChange: false
            });
        }
    }
    const textInputChange3 = (old) =>{
        if(old != 0){
            setData({
                ...data,
                old_peak: old,
                check_textInputChange: true
            });
        }else{
            setData({
                ...data,
                old_peak: old,
                check_textInputChange: false
            });
        }
    }
    const textInputChange4 = (rest) =>{
        if(rest != 0){
            setData({
                ...data,
                rest_ecf: rest,
                check_textInputChange: true
            });
        }else{
            setData({
                ...data,
                rest_ecf: rest,
                check_textInputChange: false
            });
        }
    }
    
    const registrar = async (edad,cre,old,rest) =>{
        try{
            let config = {
                method: 'POST',
                headers:{
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
            }
            let res = await fetch('http://162.214.187.236:8000/clasificaSVM?edad='+ edad + '&creatinine='+cre+'&old_peak='+ old+'&rest_ecf='+rest + '',config)
            let json = await res.json()
            console.log(json)
            alert(json.error);
        }catch(error){}
            
    }


    return(
        
            <ScrollView style={form.container}>
                <Text style={form.label}>Edad</Text>
                <View style={form.input}>
                    <FontAwesome
                    name= "calendar-check-o"
                    color="#05375a"
                    size={20}></FontAwesome>
                    <TextInput
                    placeholder="18"
                    style={form.textInput}
                    autoCapitalize="none"
                    onChangeText={(edad)=>textInputChange1(edad)}></TextInput>
                </View>

                <Text style={{color:'#000000', paddingTop: 1, fontSize:18}}>Los siguientes datos deben ser obtenidos del estudio de electrocardiograma</Text>

                <Text style={form.label}>Creatinina</Text>
                <View style={form.input}>
                    <FontAwesome
                    name= "tint"
                    color="#05375a"
                    size={20}></FontAwesome>
                    <TextInput
                    placeholder="Hombre o mujer"
                    style={form.textInput}
                    autoCapitalize="none"
                    onChangeText={(cre)=>textInputChange2(cre)}></TextInput>
                </View>

                <Text style={form.label}>Old Peak</Text>
                <View style={form.input}>
                    <FontAwesome
                    name= "tint"
                    color="#05375a"
                    size={20}></FontAwesome>
                    <TextInput
                    placeholder="120"
                    style={form.textInput}
                    autoCapitalize="none"
                    onChangeText={(old)=>textInputChange3(old)}></TextInput>
                </View>

                <Text style={form.label}>Rest ECF</Text>
                <View style={form.input}>
                    <FontAwesome
                    name= "cube"
                    color="#05375a"
                    size={20}></FontAwesome>
                    <TextInput
                    placeholder="120"
                    style={form.textInput}
                    autoCapitalize="none"
                    onChangeText={(rest)=>textInputChange4(rest)}></TextInput>
                </View>

                
                <View style={form.buton}>
                    <TouchableOpacity 
                    onPress={()=>{
                        registrar(data.edad, data.creatinine, data.old_peak,data.rest_ecf)
                    }}>
                    <Text style={form.textButon}>Enviar</Text>
                    </TouchableOpacity>
                </View>
            </ScrollView>
       
    );
}
